const prices = $input.first().json
const aiNodes = $('has_ai_node').first().json.ai_nodes
const aiNodesInfo = []
const nodesData = $('get_execution_info').first().json.data.resultData.runData
const nodeWorkflowData = $('get_execution_info').first().json.workflowData.nodes


for(let i = 0; i < aiNodes.length; i++) {
  const aiNode = aiNodes[i]
  const nodeData = nodesData[aiNode.node_name]
  for(let y = 0; y < nodeWorkflowData.length ; y ++) {
    if(aiNode.node_name == nodeWorkflowData[y].name) {
      aiNodesInfo.push(getAiNodesInfo(aiNode, nodeWorkflowData[y], nodeData))
    }
  }
}

function getAiNodesInfo(node, workflowData, nodeData){
  if(node.model_used == null) {
    return {
      "name" : node.node_name,
      "type" : workflowData.type,
      "execution_time" : nodeData[0].executionTime
    }
  } else {
    return {
    "name" : node.node_name,
    "type" : workflowData.type,
    "execution_time" : nodeData[0].executionTime,
    "ai_details" : {
      "model_used" : node.model_used,
      "completion_tokens" : node.completion_tokens,
      "prompt_tokens" : node.prompt_tokens,
      "total_tokens" : node.total_tokens,
      "cost_execution" : getExecutionCost(node)
    }
  }
  }
}

function getExecutionCost(node){
    const completionTokens = node.completion_tokens
    const promptTokens = node.prompt_tokens
    let price = {}

    const model = node.model_used;
    if(model == 'deepseek-chat' || model == 'deepseek-reasoner') {
      const now = new Date($('get_execution_info1').first().json.startedAt)
      const currentHour = now.getUTCHours()
      const currentMinute = now.getUTCMinutes()

      const totalMinutes = currentHour * 60 + currentMinute

      const startThreshold = 0 * 60 + 30
      const endThreshold = 16 * 60 + 30

      if(totalMinutes >= startThreshold && totalMinutes < endThreshold) {
        price = prices[`${model}-standard`]
      } else {
        price = prices[`${model}-discount`]
      }
    } else {
      price = prices[model];
    }
  if(price == null) {
    return 'Price not available';
  } else {
    const nodePrice = (completionTokens / 1000) * price.completion + (promptTokens / 1000) * price.prompt;
    if(nodePrice >= 0.01) {
      return `$${nodePrice.toFixed(2)}`;
    } else if(nodePrice >= 0.0001){
      return `$${nodePrice.toFixed(4)}`;
    } else {
      return `$${nodePrice.toFixed(8)}`
    }  
  }    
}

const output = aiNodesInfo

return {
  "ai_nodes" : output
}

