function getNodesInfo(data) {
  let nodesCount = 0

  const object = Object.keys(data.resultData.runData);
  object.map((node) => {
    if( data.resultData.runData[node][0].executionStatus == 'success') {
      nodesCount += 1;
      nodes.push(node)

    }
  })

  for (let i = 0; i < nodes.length; i++) {
    const element = nodes[i];

    getTypeNode(element)

  }
}

function getTypeNode(node){
  for (let index = 0; index < $input.first().json.workflowData.nodes.length; index++) {
    const element = $input.first().json.workflowData.nodes[index];
    if(element.name == node) {
      if(element.type.includes('lmChat')) {
        // AI Node
        const nodeTokenInfo = $input.first().json.data.resultData.runData[element.name][0].data.ai_languageModel[0][0].json.tokenUsage
        const modelUsed =  $input.first().json.data.resultData.runData[element.name][0].inputOverride.ai_languageModel[0][0].json.options.model
        aiNodes.push({
          "node_name" : node,
          "model_used" : modelUsed,
          "completion_tokens" : nodeTokenInfo.completionTokens,
          "prompt_tokens" : nodeTokenInfo.promptTokens,
          "total_tokens" : nodeTokenInfo.totalTokens
        })
      } else if( element.type.includes('agent')) {
        aiNodes.push({
          "node_name" : node,
          "executionTime" :  $input.first().json.data.resultData.runData[element.name][0].executionTime
        })
      } 

      else if(element.type.includes('toolWorkflow') || element.type.includes('executeWorkflow') && !element.type.includes('executeWorkflowTrigger')) {
        let nodeObject = {}
        const runDataNode = $input.first().json.data.resultData.runData[element.name][0].metadata.subExecution
        nodeObject["nombre"] = node
        nodeObject["execution_id"] = runDataNode.executionId
        nodeObject["workflow_id"] = runDataNode.workflowId
        subworkflowNodes.push(nodeObject)
      } else if(element.type.includes('executeWorkflowTrigger')){
        break
      }
      else {
        // Non AI Workflow
        nonAiNodes.push(node)
      }
    } 
  }

}

function formatDate(date){
    const dateO = new Date(date);

  const day = String(dateO.getUTCDate()).padStart(2, '0');
  const month = String(dateO.getUTCMonth() + 1).padStart(2, '0');
  const year = dateO.getUTCFullYear();

  const hours = String(dateO.getUTCHours()).padStart(2, '0');
  const minutes = String(dateO.getUTCMinutes()).padStart(2, '0');
  const seconds = String(dateO.getUTCSeconds()).padStart(2, '0');

  return `${day}/${month}/${year} - ${hours}:${minutes}:${seconds}`;
}
function getExecutionDuration(started, ended) {
  const startDate = new Date(started)
  const endDate = new Date(ended)
  const durationMS = endDate - startDate
  return durationMS / 1000;
}


