const options = {
  timeZone: "America/Denver",
  month: "short", 
  day: "2-digit", 
  hour: "numeric", 
  minute: "2-digit",
  hour12: false
};
const formatter = new Intl.DateTimeFormat("en-US", options);
const dat = items[0].json['history'];

const textBlock = dat.map(entry => {
  const lines = Object.keys(entry).map(key => {
    let value = "";
    if (key === "created_at") {
      let dat = new Date(entry[key]);
      value = `${formatter.format(dat)}`;
    } else if (key === "files") {
      if (entry[key] !== null) {
        value = `Attachments: ${entry[key]}`;
      }
    } else if (key === "chatInput") {
      value = `USER: ${entry[key]}`; // Convert to all caps
    } else if (key === "response") {
        value = `AI: ${entry[key]}`;
    } else {
      value = entry[key] !== null ? entry[key] : 'null'; 
    }
    return value;
  });
  return lines.filter(Boolean).join('\n'); // Filter out empty strings from "files"
}).join('\n\n');

return {"history": textBlock};

